import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Wifi, 
  WifiOff, 
  RefreshCw, 
  Database, 
  Clock, 
  AlertCircle,
  CheckCircle,
  Trash2,
  Download,
  Upload
} from 'lucide-react';
import { useTheme } from '../../../contexts/ThemeContext';
import { useOffline } from '../../../contexts/OfflineContext';

interface OfflineStatusWidgetProps {
  className?: string;
}

const OfflineStatusWidget: React.FC<OfflineStatusWidgetProps> = ({ className = '' }) => {
  const { resolvedTheme } = useTheme();
  const { 
    isOnline, 
    pendingActions, 
    syncPendingActions, 
    getCacheSize, 
    getLastSyncTime,
    clearCache 
  } = useOffline();

  const [isExpanded, setIsExpanded] = useState(false);
  const [isSyncing, setIsSyncing] = useState(false);

  const cacheSize = getCacheSize();
  const lastSync = getLastSyncTime();
  const pendingCount = pendingActions.length;

  const handleSync = async () => {
    setIsSyncing(true);
    try {
      await syncPendingActions();
    } finally {
      setIsSyncing(false);
    }
  };

  const formatLastSync = (date: Date | null) => {
    if (!date) return 'Jamais';

    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / (1000 * 60));

    if (minutes < 1) return '√Ä l\'instant';
    if (minutes < 60) return `${minutes}min`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h`;
    const days = Math.floor(hours / 24);
    return `${days}j`;
  };

  const getStatusColor = () => {
    if (!isOnline) return 'text-orange-500';
    if (pendingCount > 0) return 'text-yellow-500';
    return 'text-green-500';
  };

  const getStatusText = () => {
    if (!isOnline) return 'Hors ligne';
    if (pendingCount > 0) return `${pendingCount} en attente`;
    return 'Synchronis√©';
  };

  return (
    <div className={`h-full ${className}`}>
      {/* En-t√™te principal */}
      <div className="mb-4">
        <div className="flex items-center justify-between mb-3">
          <h3 className="font-semibold flex items-center gap-2">
            {isOnline ? (
              <Wifi className="w-5 h-5 text-green-500" />
            ) : (
              <WifiOff className="w-5 h-5 text-orange-500" />
            )}
            Mode Offline
          </h3>
          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className={`p-2 rounded-lg transition-colors ${
              resolvedTheme === 'dark'
                ? 'bg-gray-700 hover:bg-gray-600 text-gray-300'
