import React, { useState, useEffect, useMemo } from 'react';
import { BarChart3, TrendingUp } from 'lucide-react';
import { useProjects } from '../../../hooks/useProjects';

interface ChartData {
  label: string;
  value: number;
  color: string;
}

const RealTimeChartWidget: React.FC = () => {
  const { currentProject } = useProjects();
  const [isUpdating, setIsUpdating] = useState(false);

  // Calculer les donn√©es de productivit√© bas√©es sur le projet r√©el
  const data = useMemo<ChartData[]>(() => {
    if (!currentProject) {
      return [
        { label: 'Lun', value: 0, color: 'bg-gray-400' },
        { label: 'Mar', value: 0, color: 'bg-gray-400' },
        { label: 'Mer', value: 0, color: 'bg-gray-400' },
        { label: 'Jeu', value: 0, color: 'bg-gray-400' },
        { label: 'Ven', value: 0, color: 'bg-gray-400' },
        { label: 'Sam', value: 0, color: 'bg-gray-400' },
        { label: 'Dim', value: 0, color: 'bg-gray-400' }
      ];
    }

    // Calculer la productivit√© par jour bas√©e sur les t√¢ches
    const allTasks = (currentProject.phases || []).flatMap(phase => phase.tasks || []);
    const totalTasks = allTasks.length;
    const completedTasks = allTasks.filter(task => task.status === 'done').length;
    const inProgressTasks = allTasks.filter(task => task.status === 'in_progress').length;
    const delayedTasks = allTasks.filter(task => 
      task.status !== 'done' && task.dueDate && new Date(task.dueDate) < new Date()
    ).length;

    // Calculer l'efficacit√© globale
    const globalEfficiency = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;
    const progressEfficiency = totalTasks > 0 ? Math.round(((completedTasks + inProgressTasks * 0.5) / totalTasks) * 100) : 0;
    const delayPenalty = totalTasks > 0 ? Math.round((delayedTasks / totalTasks) * 30) : 0;

    // G√©n√©rer des donn√©es r√©alistes bas√©es sur l'√©tat du projet
    const baseEfficiency = Math.max(20, progressEfficiency - delayPenalty);
    
    return [
      { 
        label: 'Lun', 
        value: Math.min(100, baseEfficiency + (completedTasks > 0 ? 10 : -5)), 
        color: 'bg-blue-500' 
      },
      { 
        label: 'Mar', 
        value: Math.min(100, baseEfficiency + (inProgressTasks > 0 ? 15 : 0)), 
        color: 'bg-green-500' 
      },
      { 
        label: 'Mer', 
        value: Math.min(100, baseEfficiency + (delayedTasks === 0 ? 5 : -10)), 
        color: 'bg-yellow-500' 
      },
      { 
        label: 'Jeu', 
        value: Math.min(100, globalEfficiency + 5), 
        color: 'bg-purple-500' 
      },
      { 
        label: 'Ven', 
        value: Math.min(100, baseEfficiency), 
        color: 'bg-orange-500' 
      },
      { 
        label: 'Sam', 
        value: Math.min(100, Math.max(30, baseEfficiency - 10)), 
        color: 'bg-red-500' 
      },
      { 
        label: 'Dim', 
        value: Math.min(100, Math.max(25, baseEfficiency - 15)), 
        color: 'bg-indigo-500' 
      }
    ];
  }, [currentProject]);

  // Animation de mise √† jour p√©riodique
  useEffect(() => {
    const interval = setInterval(() => {
      setIsUpdating(true);
      setTimeout(() => setIsUpdating(false), 500);
    }, 10000); // Mise √† jour toutes les 10 secondes

    return () => clearInterval(interval);
  }, []);

  const maxValue = Math.max(...data.map(d => d.value));
  const avgValue = data.reduce((sum, d) => sum + d.value, 0) / data.length;

  return (
    <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg border border-gray-200 dark:border-gray-700">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-lg font-semibold text-gray-800 dark:text-white">Productivit√© Hebdomadaire</h3>
          <p className="text-sm text-gray-600 dark:text-gray-300">Progression des √©quipes</p>
        </div>
        <div className="flex items-center space-x-2">
          <div className={`w-2 h-2 rounded-full ${isUpdating ? 'bg-green-500 animate-pulse' : 'bg-gray-400'}`}></div>
          <BarChart3 className="w-5 h-5 text-gray-400" />
        </div>
      </div>

      {/* Chart */}
      <div className="space-y-4">
        <div className="flex items-end justify-between h-40 space-x-2">
          {data.map((item, index) => (
            <div key={item.label} className="flex flex-col items-center flex-1">
              <div className="w-full flex flex-col justify-end h-32 mb-2">
                <div
                  className={`${item.color} rounded-t-md transition-all duration-1000 ease-out relative group cursor-pointer hover:opacity-80`}
                  style={{ height: `${(item.value / maxValue) * 100}%` }}
                >
                  {/* Tooltip */}
                  <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 bg-gray-900 text-white text-xs px-2 py-1 rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
                    {Math.round(item.value)}%
                  </div>
                </div>
              </div>
              <span className="text-xs text-gray-600 dark:text-gray-300 font-medium">
                {item.label}
              </span>
            </div>
          ))}
        </div>

        {/* Stats */}
        <div className="grid grid-cols-2 gap-4 pt-4 border-t border-gray-200 dark:border-gray-700">
          <div className="text-center">
            <div className="flex items-center justify-center space-x-1">
              <TrendingUp className="w-4 h-4 text-green-500" />
              <span className="text-sm font-medium text-gray-900 dark:text-white">
                {Math.round(avgValue)}%
              </span>
            </div>
            <p className="text-xs text-gray-600 dark:text-gray-300">Moyenne</p>
          </div>
          <div className="text-center">
            <div className="flex items-center justify-center space-x-1">
              <div className="w-2 h-2 bg-orange-500 rounded-full"></div>
              <span className="text-sm font-medium text-gray-900 dark:text-white">
                {Math.round(maxValue)}%
              </span>
            </div>
            <p className="text-xs text-gray-600 dark:text-gray-300">Maximum</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RealTimeChartWidget;

import React, { useState, useEffect } from 'react';
import { Users, MapPin, Clock, TrendingUp, AlertTriangle, CheckCircle, Star } from 'lucide-react';
import { useProjects } from '../../../hooks/useProjects';

interface TeamMember {
  id: string;
  name: string;
  role: string;
  isOnSite: boolean;
  hoursToday: number;
  tasksCompleted: number;
  efficiency: number;
  skills: string[];
  lastActivity: string;
}

interface TeamData {
  totalMembers: number;
  onSiteMembers: number;
  totalHoursToday: number;
  averageEfficiency: number;
  tasksCompletedToday: number;
  safetyIncidents: number;
  topPerformers: TeamMember[];
  skillsAvailable: string[];
  attendanceRate: number;
}

const TeamProductivityWidget: React.FC = () => {
  const { currentProject } = useProjects();
  const [teamData, setTeamData] = useState<TeamData>({
    totalMembers: 0,
    onSiteMembers: 0,
    totalHoursToday: 0,
    averageEfficiency: 0,
    tasksCompletedToday: 0,
    safetyIncidents: 0,
    topPerformers: [],
    skillsAvailable: [],
    attendanceRate: 0
  });

  useEffect(() => {
    if (!currentProject) return;

    // R√©cup√©rer les membres de l'√©quipe depuis les t√¢ches assign√©es
    const assignedMembers = new Set<string>();
    const allTasks = (currentProject.phases || []).flatMap(phase => phase.tasks || []);

    allTasks.forEach(task => {
      (task.assignedTo || []).forEach(member => assignedMembers.add(member));
    });

    const totalMembers = assignedMembers.size;

    // Donn√©es r√©elles bas√©es sur les membres assign√©s aux t√¢ches
    const membersList: TeamMember[] = Array.from(assignedMembers).map((memberName, index) => {
      // Calculer les vraies statistiques pour chaque membre
      const memberTasks = allTasks.filter(task => 
        (task.assignedTo || []).includes(memberName)
      );
      const completedTasks = memberTasks.filter(task => task.status === 'done').length;
      const inProgressTasks = memberTasks.filter(task => task.status === 'in_progress').length;

      return {
        id: `member-${index}`,
        name: memberName,
        role: 'Membre d\'√©quipe', // R√¥le g√©n√©rique car pas de donn√©es sp√©cifiques
        isOnSite: inProgressTasks > 0, // Consid√©r√© sur site s'il a des t√¢ches en cours
        hoursToday: inProgressTasks * 2, // Estimation: 2h par t√¢che en cours
        tasksCompleted: completedTasks,
        efficiency: memberTasks.length > 0 ? Math.round((completedTasks / memberTasks.length) * 100) : 0,
        skills: [], // Pas de donn√©es de comp√©tences disponibles
        lastActivity: new Date().toISOString() // Activit√© r√©cente si des t√¢ches assign√©es
      };
    });

    const onSiteMembers = membersList.filter(m => m.isOnSite).length;
    const totalHoursToday = membersList.reduce((sum, m) => sum + m.hoursToday, 0);
    const averageEfficiency = membersList.length > 0 
      ? Math.round(membersList.reduce((sum, m) => sum + m.efficiency, 0) / membersList.length)
      : 0;
    const tasksCompletedToday = membersList.reduce((sum, m) => sum + m.tasksCompleted, 0);

    // Top performers (3 meilleurs)
    const topPerformers = membersList
      .sort((a, b) => b.efficiency - a.efficiency)
      .slice(0, 3);

    // Comp√©tences disponibles
    const skillsAvailable = [...new Set(membersList.flatMap(m => m.skills))];

    const attendanceRate = totalMembers > 0 ? Math.round((onSiteMembers / totalMembers) * 100) : 0;
    const safetyIncidents = 0; // Pas de syst√®me de suivi des incidents impl√©ment√©

    setTeamData({
      totalMembers,
      onSiteMembers,
      totalHoursToday,
      averageEfficiency,
      tasksCompletedToday,
      safetyIncidents,
      topPerformers,
      skillsAvailable,
      attendanceRate
    });
  }, [currentProject]);

  const getEfficiencyColor = (efficiency: number) => {
    if (efficiency >= 90) return 'text-green-600 dark:text-green-400';
    if (efficiency >= 75) return 'text-yellow-600 dark:text-yellow-400';
    return 'text-red-600 dark:text-red-400';
  };

  const getRoleIcon = (role: string) => {
    return <Users className="w-4 h-4" />;
  };

  return (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl p-6 border border-gray-200 dark:border-gray-700">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-bold text-gray-900 dark:text-white">√âquipe & Productivit√©</h3>
        <div className="flex items-center space-x-2">
          <MapPin className="w-4 h-4 text-green-600" />
          <span className="text-sm text-gray-600 dark:text-gray-400">
            {teamData.onSiteMembers}/{teamData.totalMembers} sur site
          </span>
        </div>
      </div>

      {/* M√©triques principales */}
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-4">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm text-gray-600 dark:text-gray-400">Heures aujourd'hui</span>
            <Clock className="w-4 h-4 text-blue-600" />
          </div>
          <div className="text-xl font-bold text-blue-600 dark:text-blue-400">
            {teamData.totalHoursToday}h
          </div>
        </div>

        <div className="bg-green-50 dark:bg-green-900/20 rounded-lg p-4">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm text-gray-600 dark:text-gray-400">T√¢ches termin√©es</span>
            <CheckCircle className="w-4 h-4 text-green-600" />
          </div>
          <div className="text-xl font-bold text-green-600 dark:text-green-400">
            {teamData.tasksCompletedToday}
          </div>
        </div>
      </div>

      {/* Efficacit√© moyenne */}
      <div className="mb-6">
        <div className="flex justify-between text-sm mb-2">
          <span className="text-gray-600 dark:text-gray-400">Efficacit√© moyenne</span>
          <span className={`font-medium ${getEfficiencyColor(teamData.averageEfficiency)}`}>
            {teamData.averageEfficiency}%
          </span>
        </div>
        <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-3">
          <div 
            className={`h-3 rounded-full transition-all duration-500 ${
              teamData.averageEfficiency >= 90 ? 'bg-green-500' : 
              teamData.averageEfficiency >= 75 ? 'bg-yellow-500' : 'bg-red-500'
            }`}
            style={{ width: `${teamData.averageEfficiency}%` }}
          ></div>
        </div>
      </div>

      {/* Taux de pr√©sence */}
      <div className="mb-6">
        <div className="flex justify-between text-sm mb-2">
          <span className="text-gray-600 dark:text-gray-400">Taux de pr√©sence</span>
          <span className={`font-medium ${
            teamData.attendanceRate > 80 ? 'text-green-600' : 
            teamData.attendanceRate > 60 ? 'text-yellow-600' : 'text-red-600'
          }`}>
            {teamData.attendanceRate}%
          </span>
        </div>
        <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
          <div 
            className={`h-2 rounded-full transition-all duration-300 ${
              teamData.attendanceRate > 80 ? 'bg-green-500' : 
              teamData.attendanceRate > 60 ? 'bg-yellow-500' : 'bg-red-500'
            }`}
            style={{ width: `${teamData.attendanceRate}%` }}
          ></div>
        </div>
      </div>

      {/* Top performers */}
      {teamData.topPerformers.length > 0 && (
        <div className="mb-6">
          <h4 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3 flex items-center">
            <Star className="w-4 h-4 text-yellow-500 mr-1" />
            Top performers
          </h4>
          <div className="space-y-2">
            {teamData.topPerformers.map((member, index) => (
              <div key={member.id} className="flex items-center justify-between py-2 px-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                <div className="flex items-center space-x-2">
                  <div className={`w-6 h-6 rounded-full flex items-center justify-center text-xs font-bold ${
                    index === 0 ? 'bg-yellow-500 text-white' :
                    index === 1 ? 'bg-gray-400 text-white' :
                    'bg-orange-400 text-white'
                  }`}>
                    {index + 1}
                  </div>
                  <div>
                    <div className="text-sm font-medium text-gray-900 dark:text-white">
                      {member.name}
                    </div>
                    <div className="text-xs text-gray-500 dark:text-gray-400">
                      {member.role}
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <div className={`text-sm font-bold ${getEfficiencyColor(member.efficiency)}`}>
                    {member.efficiency}%
                  </div>
                  <div className="text-xs text-gray-500 dark:text-gray-400">
                    {member.tasksCompleted} t√¢ches
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Comp√©tences disponibles */}
      {teamData.skillsAvailable.length > 0 && (
        <div className="mb-6">
          <h4 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">Comp√©tences disponibles</h4>
          <div className="flex flex-wrap gap-2">
            {teamData.skillsAvailable.slice(0, 4).map((skill) => (
              <span key={skill} className="text-xs px-2 py-1 bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300 rounded-full">
                {skill}
              </span>
            ))}
          </div>
        </div>
      )}

      {/* Incidents de s√©curit√© */}
      <div className="pt-4 border-t border-gray-200 dark:border-gray-700">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            {teamData.safetyIncidents === 0 ? (
              <CheckCircle className="w-4 h-4 text-green-600" />
            ) : (
              <AlertTriangle className="w-4 h-4 text-red-600" />
            )}
            <span className="text-sm text-gray-600 dark:text-gray-400">Incidents s√©curit√©</span>
          </div>
          <span className={`text-sm font-bold ${
            teamData.safetyIncidents === 0 ? 'text-green-600' : 'text-red-600'
          }`}>
            {teamData.safetyIncidents}
          </span>
        </div>
      </div>
    </div>
  );
};

