import React from 'react';
import type { Equipment } from '../../types';
import { X } from 'lucide-react';

import MaintenanceHistorySection from './MaintenanceHistorySection';
import EquipmentForm from './EquipmentForm';

interface EquipmentDetailModalProps {
  equipment: Equipment | null;
  onClose: () => void;
  onEdit?: (equipment: Equipment) => void;
  onDelete?: (equipment: Equipment) => void;
  onUpdateMaintenance?: (equipment: Equipment) => void;
}

const EquipmentDetailModal: React.FC<EquipmentDetailModalProps> = ({ equipment, onClose, onEdit, onDelete, onUpdateMaintenance }) => {
  const [editMode, setEditMode] = React.useState(false);
  const [confirmDelete, setConfirmDelete] = React.useState(false);
  const [form, setForm] = React.useState<Equipment | null>(equipment);

  React.useEffect(() => {
    setForm(equipment);
    setEditMode(false);
    setConfirmDelete(false);
  }, [equipment]);

  if (!equipment) return null;

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    if (!form) return;
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (form && onEdit) onEdit(form);
    setEditMode(false);
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-30">
      <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-xl relative">
        <button className="absolute top-4 right-4 text-gray-400 hover:text-gray-800" onClick={onClose}>
          <X className="w-6 h-6" />
        </button>
        <div className="absolute top-4 left-4 flex gap-2">
          <button className="px-3 py-1 bg-blue-100 text-blue-700 rounded hover:bg-blue-200" onClick={() => setEditMode(!editMode)}>
            {editMode ? 'Annuler' : '√âditer'}
          </button>
          <button className="px-3 py-1 bg-red-100 text-red-700 rounded hover:bg-red-200" onClick={() => setConfirmDelete(true)}>
            Supprimer
          </button>
        </div>
        <h2 className="text-2xl font-bold mb-4 text-center">{equipment.name}</h2>

        {editMode && form ? (
          <EquipmentForm
            initialValues={form}
            mode="edit"
            onCancel={() => setEditMode(false)}
            onSubmit={(values) => {
              if (onEdit) onEdit(values);
              setEditMode(false);
            }}
          />
        ) : (
          <div>
            <div className="mb-2 text-gray-700"><b>Type :</b> {equipment.type}</div>
            <div className="mb-2 text-gray-700"><b>Mod√®le :</b> {equipment.model}</div>
            <div className="mb-2 text-gray-700"><b>Num√©ro de s√©rie :</b> {equipment.serialNumber}</div>
            <div className="mb-2 text-gray-700"><b>Statut :</b> {equipment.status}</div>
            <div className="mb-2 text-gray-700"><b>Localisation :</b> {equipment.location}</div>
            
            <div className="mb-2 text-gray-700"><b>Op√©rateur :</b> {equipment.operator}</div>
            <div className="mb-2 text-gray-700"><b>Derni√®re maintenance :</b> {equipment.lastMaintenance}</div>
            <div className="mb-2 text-gray-700"><b>Prochaine maintenance :</b> {equipment.nextMaintenance}</div>
            {equipment.coordinates && (
              <div className="mb-2 text-gray-700"><b>Coordonn√©es :</b> {equipment.coordinates.lat}, {equipment.coordinates.lng}</div>
            )}
            {equipment.assignedProject && (
              <div className="mb-2 text-gray-700"><b>Projet assign√© :</b> {equipment.assignedProject}</div>
            )}
          </div>
        )}

        {/* Section Historique des maintenances */}
        <div className="mt-6">
          <h3 className="text-lg font-bold mb-2">Historique des maintenances</h3>
          <MaintenanceHistorySection
            equipment={equipment}
            onUpdate={onUpdateMaintenance}
          />
        </div>

        {confirmDelete && (
          <div className="fixed inset-0 z-60 flex items-center justify-center bg-black bg-opacity-40">
            <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-xs">
              <div className="mb-4 text-center text-gray-700">Confirmer la suppression de <b>{equipment.name}</b> ?</div>
              <div className="flex gap-3 justify-center">
                <button className="px-4 py-2 bg-gray-100 rounded" onClick={() => setConfirmDelete(false)}>Annuler</button>
                <button className="px-4 py-2 bg-red-600 text-white rounded" onClick={() => onDelete && onDelete(equipment)}>Supprimer</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EquipmentDetailModal;

import React, { useState } from 'react';
import type { Equipment, MaintenanceEvent } from '../../contexts/projectTypes';
import { Plus, Trash2 } from 'lucide-react';

interface MaintenanceHistorySectionProps {
  equipment: Equipment | null;
  onUpdate?: (equipment: Equipment) => void;
}

const emptyEvent: Omit<MaintenanceEvent, 'id'> = {
  date: '',
  type: '',
  description: '',
  operator: '',
};

export const MaintenanceHistorySection: React.FC<MaintenanceHistorySectionProps> = ({ equipment, onUpdate }) => {
  const [adding, setAdding] = useState(false);
  const [newEvent, setNewEvent] = useState(emptyEvent);

  if (!equipment) return null;
  const history = equipment.maintenanceHistory || [];

  const handleAdd = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newEvent.date || !newEvent.type || !newEvent.description) return;
    const event: MaintenanceEvent = {
      ...newEvent,
      id: Date.now().toString(),
    };
    const updated = {
      ...equipment,
      maintenanceHistory: [...history, event],
    };
    onUpdate && onUpdate(updated);
    setNewEvent(emptyEvent);
    setAdding(false);
  };

  const handleDelete = (id: string) => {
    const updated = {
      ...equipment,
      maintenanceHistory: history.filter(ev => ev.id !== id),
    };
    onUpdate && onUpdate(updated);
  };

  return (
    <div>
      <ul className="mb-2">
        {history.length === 0 && <li className="text-gray-500">Aucune maintenance enregistr√©e.</li>}
        {history.map(ev => (
          <li key={ev.id} className="flex items-center justify-between border-b py-1 text-sm">
            <span>
              <span className="font-medium">{ev.date}</span> ‚Äì <span>{ev.type}</span> : {ev.description} {ev.operator && (<span className="text-gray-500">({ev.operator})</span>)}
            </span>
            <button onClick={() => handleDelete(ev.id)} className="ml-2 text-red-500 hover:text-red-700"><Trash2 size={16} /></button>
          </li>
        ))}
      </ul>
      {adding ? (
        <form onSubmit={handleAdd} className="flex flex-col gap-2 mb-2">
          <input className="border rounded px-2 py-1" type="date" value={newEvent.date} onChange={e => setNewEvent({ ...newEvent, date: e.target.value })} required />
          <input className="border rounded px-2 py-1" placeholder="Type (r√©vision, r√©paration...)" value={newEvent.type} onChange={e => setNewEvent({ ...newEvent, type: e.target.value })} required />
          <input className="border rounded px-2 py-1" placeholder="Description" value={newEvent.description} onChange={e => setNewEvent({ ...newEvent, description: e.target.value })} required />
          <input className="border rounded px-2 py-1" placeholder="Op√©rateur" value={newEvent.operator} onChange={e => setNewEvent({ ...newEvent, operator: e.target.value })} />
          <div className="flex gap-2">
            <button className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700" type="submit">Ajouter</button>
            <button className="bg-gray-200 px-2 py-1 rounded" type="button" onClick={() => setAdding(false)}>Annuler</button>
          </div>
        </form>
      ) : (
        <button className="flex items-center gap-1 text-blue-700 hover:underline" onClick={() => setAdding(true)}>
          <Plus size={16} /> Ajouter une maintenance
        </button>
      )}
    </div>
  );
};

export default MaintenanceHistorySection;

import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Camera, 
  Scan, 
  X, 
  Flashlight, 
  FlashlightOff, 
  RotateCcw,
  CheckCircle,
  AlertCircle,
  Package,
  MapPin,
  Calendar,
  User
} from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

interface Equipment {
  id: string;
  name: string;
  type: string;
  serialNumber: string;
  status: 'available' | 'in-use' | 'maintenance' | 'damaged';
  location: string;
  assignedTo?: string;
  lastMaintenance?: Date;
  nextMaintenance?: Date;
  qrCode: string;
  barcode?: string;
}

interface QRScannerProps {
  isOpen: boolean;
  onClose: () => void;
  onScanSuccess: (equipment: Equipment) => void;
  onScanError?: (error: string) => void;
}

// Simulation d'√©quipements pour la d√©mo
const mockEquipments: Equipment[] = [
  {
    id: 'eq-001',
    name: 'Pelleteuse CAT 320',
    type: 'Engin de chantier',
    serialNumber: 'CAT320-2024-001',
    status: 'available',
    location: 'Chantier Dakar Nord',
    qrCode: 'QR001',
    barcode: '1234567890123',
    lastMaintenance: new Date('2024-07-01'),
    nextMaintenance: new Date('2024-08-15'),
  },
  {
    id: 'eq-002',
    name: 'Grue mobile 50T',
    type: 'Engin de levage',
    serialNumber: 'GROVE-RT550-002',
    status: 'in-use',
    location: 'Chantier Almadies',
    assignedTo: 'Mamadou Diallo',
    qrCode: 'QR002',
    barcode: '2345678901234',
    lastMaintenance: new Date('2024-06-15'),
    nextMaintenance: new Date('2024-09-01'),
  },
  {
    id: 'eq-003',
    name: 'B√©tonni√®re 500L',
    type: '√âquipement de construction',
    serialNumber: 'BET500-2024-003',
    status: 'maintenance',
    location: 'Atelier central',
    qrCode: 'QR003',
    barcode: '3456789012345',
    lastMaintenance: new Date('2024-07-20'),
    nextMaintenance: new Date('2024-08-20'),
  },
];

const QRScanner: React.FC<QRScannerProps> = ({
  isOpen,
  onClose,
  onScanSuccess,
  onScanError,
}) => {
  const { resolvedTheme } = useTheme();
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isScanning, setIsScanning] = useState(false);
  const [hasCamera, setHasCamera] = useState(false);
  const [flashlightOn, setFlashlightOn] = useState(false);
  const [scanResult, setScanResult] = useState<Equipment | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [manualInput, setManualInput] = useState('');
  const [showManualInput, setShowManualInput] = useState(false);

  // Initialiser la cam√©ra
  useEffect(() => {
    if (isOpen && !isScanning) {
      startCamera();
    }
    return () => {
      stopCamera();
    };
  }, [isOpen]);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { 
          facingMode: 'environment', // Cam√©ra arri√®re
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        setHasCamera(true);
        setIsScanning(true);
        setError(null);

        // D√©marrer la d√©tection de QR codes
        startQRDetection();
      }
    } catch (err) {
      console.error('Erreur d\'acc√®s √† la cam√©ra:', err);
      setError('Impossible d\'acc√©der √† la cam√©ra. V√©rifiez les permissions.');
      setHasCamera(false);
    }
  };

  const stopCamera = () => {
    if (videoRef.current?.srcObject) {
      const stream = videoRef.current.srcObject as MediaStream;
      stream.getTracks().forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setIsScanning(false);
    setHasCamera(false);
  };

  const startQRDetection = () => {
    const detectQR = () => {
      if (!videoRef.current || !canvasRef.current || !isScanning) return;

      const video = videoRef.current;
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      if (!ctx || video.videoWidth === 0) {
        requestAnimationFrame(detectQR);
        return;
      }

      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      ctx.drawImage(video, 0, 0);

      // Simulation de d√©tection QR (en production, utiliser une lib comme jsQR)
      // Pour la d√©mo, on simule une d√©tection al√©atoire
      if (Math.random() < 0.01) { // 1% de chance par frame
        const randomEquipment = mockEquipments[Math.floor(Math.random() * mockEquipments.length)];
        handleScanSuccess(randomEquipment);
        return;
      }

      if (isScanning) {
        requestAnimationFrame(detectQR);
      }
    };

    requestAnimationFrame(detectQR);
  };

  const handleScanSuccess = (equipment: Equipment) => {
    setScanResult(equipment);
    setIsScanning(false);

    // Vibration si support√©e
    if (navigator.vibrate) {
      navigator.vibrate(200);
    }

    // Appeler le callback apr√®s un d√©lai pour montrer le r√©sultat
    setTimeout(() => {
      onScanSuccess(equipment);
      onClose();
    }, 2000);
  };

  const handleManualInput = () => {
    if (!manualInput.trim()) return;

    // Chercher l'√©quipement par QR code ou code-barres
    const equipment = mockEquipments.find(eq => 
      eq.qrCode === manualInput || eq.barcode === manualInput
    );

    if (equipment) {
      handleScanSuccess(equipment);
    } else {
      setError('Code non reconnu. V√©rifiez le code saisi.');
      onScanError?.('Code non reconnu');
    }
  };

  const toggleFlashlight = async () => {
    try {
      const stream = videoRef.current?.srcObject as MediaStream;
      const track = stream?.getVideoTracks()[0];

      if (track && 'torch' in track.getCapabilities()) {
        await track.applyConstraints({
          advanced: [{ torch: !flashlightOn } as any]
        });
        setFlashlightOn(!flashlightOn);
      }
    } catch (err) {
      console.error('Erreur flash:', err);
    }
  };

  const getStatusColor = (status: Equipment['status']) => {
    switch (status) {
      case 'available': return 'text-green-500';
      case 'in-use': return 'text-blue-500';
      case 'maintenance': return 'text-yellow-500';
      case 'damaged': return 'text-red-500';
      default: return 'text-gray-500';
    }
  };

  const getStatusLabel = (status: Equipment['status']) => {
    switch (status) {
      case 'available': return 'Disponible';
      case 'in-use': return 'En cours d\'utilisation';
      case 'maintenance': return 'En maintenance';
      case 'damaged': return 'Endommag√©';
      default: return 'Inconnu';
    }
  };

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <motion.div
        className="fixed inset-0 bg-black/90 flex items-center justify-center z-50"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <motion.div
          className={`
            relative w-full max-w-md mx-4 rounded-2xl overflow-hidden
            ${resolvedTheme === 'dark'
              ? 'bg-gray-900 border border-gray-700'
              : 'bg-white border border-gray-200'
            }
          `}
          initial={{ scale: 0.9, y: 20 }}
          animate={{ scale: 1, y: 0 }}
          exit={{ scale: 0.9, y: 20 }}
        >
          {/* Header */}
          <div className={`
            flex items-center justify-between p-4 border-b
            ${resolvedTheme === 'dark' ? 'border-gray-700' : 'border-gray-200'}
          `}>
            <div className="flex items-center gap-2">
              <Scan className="w-5 h-5 text-blue-500" />
              <h2 className="font-semibold">Scanner √âquipement</h2>
            </div>
            <button
              onClick={onClose}
              className="p-1 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
            >
              <X className="w-5 h-5" />
            </button>
          </div>

          {/* Contenu principal */}
          <div className="p-4">
            {/* R√©sultat du scan */}
            {scanResult ? (
              <motion.div
                className="text-center space-y-4"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
              >
                <div className="flex justify-center">
                  <CheckCircle className="w-16 h-16 text-green-500" />
                </div>

                <div>
                  <h3 className="text-lg font-semibold text-green-600">
                    √âquipement d√©tect√© !
                  </h3>
                  <p className="text-sm opacity-70">
                    Redirection en cours...
                  </p>
                </div>

                <div className={`
                  p-4 rounded-lg border text-left
                  ${resolvedTheme === 'dark'
                    ? 'bg-gray-800 border-gray-700'
                    : 'bg-gray-50 border-gray-200'
                  }
                `}>
                  <div className="flex items-center gap-2 mb-2">
                    <Package className="w-4 h-4" />
                    <span className="font-medium">{scanResult.name}</span>
                  </div>

                  <div className="space-y-1 text-sm opacity-70">
                    <div className="flex items-center gap-2">
                      <span>Statut:</span>
                      <span className={getStatusColor(scanResult.status)}>
                        {getStatusLabel(scanResult.status)}
                      </span>
                    </div>
                    <div className="flex items-center gap-2">
                      <MapPin className="w-3 h-3" />
                      <span>{scanResult.location}</span>
                    </div>
                    {scanResult.assignedTo && (
                      <div className="flex items-center gap-2">
                        <User className="w-3 h-3" />
                        <span>Assign√© √†: {scanResult.assignedTo}</span>
                      </div>
                    )}
                  </div>
                </div>
              </motion.div>
            ) : (
              <>
                {/* Zone de scan */}
                <div className="relative mb-4">
                  {hasCamera && !error ? (
                    <div className="relative">
                      <video
                        ref={videoRef}
                        className="w-full h-64 object-cover rounded-lg bg-black"
                        playsInline
                        muted
                      />
                      <canvas ref={canvasRef} className="hidden" />

                      {/* Overlay de scan */}
                      <div className="absolute inset-0 flex items-center justify-center">
                        <motion.div
                          className="w-48 h-48 border-2 border-white rounded-lg"
                          animate={{
                            boxShadow: [
                              '0 0 0 0 rgba(59, 130, 246, 0.7)',
                              '0 0 0 10px rgba(59, 130, 246, 0)',
                            ],
                          }}
                          transition={{
                            duration: 1.5,
                            repeat: Infinity,
                            ease: 'easeOut',
                          }}
                        >
                          <div className="absolute top-0 left-0 w-6 h-6 border-t-4 border-l-4 border-blue-500 rounded-tl-lg" />
                          <div className="absolute top-0 right-0 w-6 h-6 border-t-4 border-r-4 border-blue-500 rounded-tr-lg" />
                          <div className="absolute bottom-0 left-0 w-6 h-6 border-b-4 border-l-4 border-blue-500 rounded-bl-lg" />
                          <div className="absolute bottom-0 right-0 w-6 h-6 border-b-4 border-r-4 border-blue-500 rounded-br-lg" />
                        </motion.div>
                      </div>

                      {/* Instructions */}
                      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white text-center">
                        <p className="text-sm bg-black/50 px-3 py-1 rounded-full">
                          Pointez vers un QR code ou code-barres
                        </p>
                      </div>
                    </div>
                  ) : (
                    <div className={`
                      w-full h-64 rounded-lg border-2 border-dashed flex flex-col items-center justify-center
                      ${resolvedTheme === 'dark'
                        ? 'border-gray-600 bg-gray-800'
                        : 'border-gray-300 bg-gray-50'
                      }
                    `}>
                      <Camera className="w-12 h-12 text-gray-400 mb-2" />
                      <p className="text-sm text-gray-500 text-center">
                        {error || 'Cam√©ra non disponible'}
                      </p>
                      <button
                        onClick={startCamera}
                        className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                      >
                        <RotateCcw className="w-4 h-4 inline mr-2" />
                        R√©essayer
                      </button>
                    </div>
                  )}
                </div>

                {/* Contr√¥les */}
                {hasCamera && (
                  <div className="flex justify-center gap-4 mb-4">
                    <button
                      onClick={toggleFlashlight}
                      className={`
                        p-3 rounded-full transition-colors
                        ${flashlightOn
                          ? 'bg-yellow-500 text-white'
                          : resolvedTheme === 'dark'
                            ? 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                            : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
                        }
                      `}
                    >
                      {flashlightOn ? (
                        <Flashlight className="w-5 h-5" />
                      ) : (
                        <FlashlightOff className="w-5 h-5" />
                      )}
                    </button>
                  </div>
                )}

                {/* Saisie manuelle */}
                <div className="space-y-3">
                  <button
                    onClick={() => setShowManualInput(!showManualInput)}
                    className="w-full text-sm text-blue-500 hover:text-blue-600 transition-colors"
                  >
                    {showManualInput ? 'Masquer' : 'Saisir'} le code manuellement
                  </button>

                  {showManualInput && (
                    <motion.div
                      initial={{ opacity: 0, height: 0 }}
                      animate={{ opacity: 1, height: 'auto' }}
                      exit={{ opacity: 0, height: 0 }}
                      className="space-y-3"
                    >
                      <input
                        type="text"
                        value={manualInput}
                        onChange={(e) => setManualInput(e.target.value)}
                        placeholder="QR code ou code-barres"
                        className={`
                          w-full px-3 py-2 rounded-lg border
                          ${resolvedTheme === 'dark'
                            ? 'bg-gray-800 border-gray-600 text-white'
                            : 'bg-white border-gray-300 text-gray-900'
                          }
                        `}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter') {
                            handleManualInput();
                          }
                        }}
                      />
                      <button
                        onClick={handleManualInput}
                        disabled={!manualInput.trim()}
                        className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                      >
                        Rechercher
                      </button>
                    </motion.div>
                  )}
                </div>

                {/* Codes d'exemple pour la d√©mo */}
                <div className={`
                  mt-4 p-3 rounded-lg text-xs
                  ${resolvedTheme === 'dark'
                    ? 'bg-gray-800 border border-gray-700'
                    : 'bg-gray-50 border border-gray-200'
                  }
                `}>
                  <p className="font-medium mb-2">üéØ Codes de test :</p>
                  <div className="space-y-1 opacity-70">
                    <p>‚Ä¢ QR001 - Pelleteuse CAT 320</p>
                    <p>‚Ä¢ QR002 - Grue mobile 50T</p>
                    <p>‚Ä¢ QR003 - B√©tonni√®re 500L</p>
                  </div>
                </div>
              </>
            )}

            {/* Erreur */}
            {error && !scanResult && (
              <motion.div
                className="flex items-center gap-2 p-3 bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 rounded-lg mt-4"
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
              >
                <AlertCircle className="w-4 h-4" />
                <span className="text-sm">{error}</span>
              </motion.div>
            )}
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default QRScanner;

