import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';

export interface LocationData {
  latitude: number;
  longitude: number;
  accuracy: number;
  timestamp: Date;
  address?: string;
}

export interface TeamMember {
  id: string;
  name: string;
  role: string;
  avatar?: string;
  isOnline: boolean;
  lastSeen: Date;
  currentLocation?: LocationData;
  assignedSite?: string;
}

export interface GeofenceZone {
  id: string;
  name: string;
  type: 'site' | 'office' | 'warehouse' | 'restricted';
  center: { lat: number; lng: number };
  radius: number; // en m√®tres
  isActive: boolean;
  notifications: {
    onEnter: boolean;
    onExit: boolean;
    onLongStay: boolean;
  };
  allowedMembers?: string[];
}

export interface LocationEvent {
  id: string;
  memberId: string;
  type: 'enter' | 'exit' | 'arrival' | 'departure' | 'alert';
  zoneId?: string;
  location: LocationData;
  timestamp: Date;
  message: string;
}

interface GeolocationContextType {
  // √âtat de g√©olocalisation
  isLocationEnabled: boolean;
  currentLocation: LocationData | null;
  locationError: string | null;
  isTracking: boolean;

  // √âquipe et suivi
  teamMembers: TeamMember[];
  geofenceZones: GeofenceZone[];
  locationEvents: LocationEvent[];

  // Actions de g√©olocalisation
  enableLocation: () => Promise<boolean>;
  disableLocation: () => void;
  startTracking: () => void;
  stopTracking: () => void;
  getCurrentPosition: () => Promise<LocationData | null>;

  // Gestion des √©quipes
  updateMemberLocation: (memberId: string, location: LocationData) => void;
  setMemberOnline: (memberId: string, isOnline: boolean) => void;

  // Gestion des g√©ofences
  addGeofenceZone: (zone: Omit<GeofenceZone, 'id'>) => void;
  updateGeofenceZone: (zoneId: string, updates: Partial<GeofenceZone>) => void;
  removeGeofenceZone: (zoneId: string) => void;
  checkGeofenceViolations: (memberId: string, location: LocationData) => void;

  // Utilitaires
  calculateDistance: (point1: LocationData, point2: LocationData) => number;
  getAddressFromCoordinates: (lat: number, lng: number) => Promise<string>;
  getNearbyMembers: (location: LocationData, radiusKm: number) => TeamMember[];
}

const GeolocationContext = createContext<GeolocationContextType | undefined>(undefined);

export const useGeolocation = () => {
  const context = useContext(GeolocationContext);
  if (!context) {
    throw new Error('useGeolocation must be used within a GeolocationProvider');
  }
  return context;
};

interface GeolocationProviderProps {
  children: React.ReactNode;
}

export const GeolocationProvider: React.FC<GeolocationProviderProps> = ({ children }) => {
  const [isLocationEnabled, setIsLocationEnabled] = useState(false);
  const [currentLocation, setCurrentLocation] = useState<LocationData | null>(null);
  const [locationError, setLocationError] = useState<string | null>(null);
  const [isTracking, setIsTracking] = useState(false);
  const [teamMembers, setTeamMembers] = useState<TeamMember[]>([]);
  const [geofenceZones, setGeofenceZones] = useState<GeofenceZone[]>([]);
  const [locationEvents, setLocationEvents] = useState<LocationEvent[]>([]);
  const [watchId, setWatchId] = useState<number | null>(null);

  // V√©rifier la disponibilit√© de la g√©olocalisation
  useEffect(() => {
    if (!navigator.geolocation) {
      setLocationError('La g√©olocalisation n\'est pas support√©e par ce navigateur');
    }
  }, []);

  // Charger les donn√©es depuis localStorage
  useEffect(() => {
    const savedZones = localStorage.getItem('geofence-zones');
    const savedMembers = localStorage.getItem('team-members');
    const savedEvents = localStorage.getItem('location-events');

    if (savedZones) {
      try {
        setGeofenceZones(JSON.parse(savedZones));
      } catch (error) {
        console.error('Erreur lors du chargement des zones:', error);
      }
    }

    if (savedMembers) {
      try {
        setTeamMembers(JSON.parse(savedMembers));
      } catch (error) {
        console.error('Erreur lors du chargement des membres:', error);
      }
    }

    if (savedEvents) {
      try {
        const events = JSON.parse(savedEvents);
        // Garder seulement les √©v√©nements des 7 derniers jours
        const weekAgo = new Date();
        weekAgo.setDate(weekAgo.getDate() - 7);
        const recentEvents = events.filter((event: LocationEvent) => 
          new Date(event.timestamp) > weekAgo
        );
        setLocationEvents(recentEvents);
      } catch (error) {
        console.error('Erreur lors du chargement des √©v√©nements:', error);
      }
    }
  }, []);

  // Sauvegarder les donn√©es dans localStorage
  useEffect(() => {
    localStorage.setItem('geofence-zones', JSON.stringify(geofenceZones));
  }, [geofenceZones]);

  useEffect(() => {
    localStorage.setItem('team-members', JSON.stringify(teamMembers));
  }, [teamMembers]);

  useEffect(() => {
    localStorage.setItem('location-events', JSON.stringify(locationEvents));
  }, [locationEvents]);

  const enableLocation = useCallback(async (): Promise<boolean> => {
    if (!navigator.geolocation) {
      setLocationError('G√©olocalisation non support√©e');
      return false;
    }

    try {
      const position = await new Promise<GeolocationPosition>((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 60000,
        });
      });

      const location: LocationData = {
        latitude: position.coords.latitude,
        longitude: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: new Date(),
      };

      setCurrentLocation(location);
      setIsLocationEnabled(true);
      setLocationError(null);
      return true;
    } catch (error: any) {
      let errorMessage = 'Erreur de g√©olocalisation';

      switch (error.code) {
        case error.PERMISSION_DENIED:
          errorMessage = 'Permission de g√©olocalisation refus√©e';
          break;
        case error.POSITION_UNAVAILABLE:
          errorMessage = 'Position non disponible';
          break;
        case error.TIMEOUT:
          errorMessage = 'Timeout de g√©olocalisation';
          break;
      }

      setLocationError(errorMessage);
      return false;
    }
  }, []);

  const disableLocation = useCallback(() => {
    if (watchId) {
      navigator.geolocation.clearWatch(watchId);
      setWatchId(null);
    }
    setIsLocationEnabled(false);
    setIsTracking(false);
    setCurrentLocation(null);
  }, [watchId]);

  const startTracking = useCallback(() => {
    if (!isLocationEnabled) return;

    const id = navigator.geolocation.watchPosition(
      (position) => {
        const location: LocationData = {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          accuracy: position.coords.accuracy,
          timestamp: new Date(),
        };

        setCurrentLocation(location);

        // V√©rifier les violations de g√©ofence pour l'utilisateur actuel
        // (assumons que l'utilisateur actuel a l'ID 'current-user')
        checkGeofenceViolations('current-user', location);
      },
      (error) => {
        console.error('Erreur de suivi:', error);
        setLocationError('Erreur lors du suivi de position');
      },
      {
        enableHighAccuracy: true,
        timeout: 30000,
        maximumAge: 10000,
      }
    );

    setWatchId(id);
    setIsTracking(true);
  }, [isLocationEnabled]);

  const stopTracking = useCallback(() => {
    if (watchId) {
      navigator.geolocation.clearWatch(watchId);
      setWatchId(null);
    }
    setIsTracking(false);
  }, [watchId]);

  const getCurrentPosition = useCallback(async (): Promise<LocationData | null> => {
    if (!navigator.geolocation) return null;

    try {
      const position = await new Promise<GeolocationPosition>((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 60000,
        });
      });

      return {
        latitude: position.coords.latitude,
        longitude: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: new Date(),
      };
    } catch (error) {
      console.error('Erreur getCurrentPosition:', error);
      return null;
    }
  }, []);

  const updateMemberLocation = useCallback((memberId: string, location: LocationData) => {
    setTeamMembers(prev => prev.map(member => 
      member.id === memberId
        ? { ...member, currentLocation: location, lastSeen: new Date() }
        : member
    ));

    // V√©rifier les violations de g√©ofence
    checkGeofenceViolations(memberId, location);
  }, []);

  const setMemberOnline = useCallback((memberId: string, isOnline: boolean) => {
    setTeamMembers(prev => prev.map(member => 
      member.id === memberId
        ? { ...member, isOnline, lastSeen: new Date() }
        : member
    ));
  }, []);

  const addGeofenceZone = useCallback((zone: Omit<GeofenceZone, 'id'>) => {
    const newZone: GeofenceZone = {
      ...zone,
      id: `zone-${Date.now()}`,
    };
    setGeofenceZones(prev => [...prev, newZone]);
  }, []);

  const updateGeofenceZone = useCallback((zoneId: string, updates: Partial<GeofenceZone>) => {
    setGeofenceZones(prev => prev.map(zone => 
      zone.id === zoneId ? { ...zone, ...updates } : zone
    ));
  }, []);

  const removeGeofenceZone = useCallback((zoneId: string) => {
    setGeofenceZones(prev => prev.filter(zone => zone.id !== zoneId));
  }, []);

  const calculateDistance = useCallback((point1: LocationData, point2: LocationData): number => {
    const R = 6371; // Rayon de la Terre en km
    const dLat = (point2.latitude - point1.latitude) * Math.PI / 180;
    const dLon = (point2.longitude - point1.longitude) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(point1.latitude * Math.PI / 180) * Math.cos(point2.latitude * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  }, []);

  const checkGeofenceViolations = useCallback((memberId: string, location: LocationData) => {
    const member = teamMembers.find(m => m.id === memberId);
    if (!member) return;

    geofenceZones.forEach(zone => {
      if (!zone.isActive) return;

      const zoneCenter = { latitude: zone.center.lat, longitude: zone.center.lng, accuracy: 0, timestamp: new Date() };
      const distance = calculateDistance(location, zoneCenter) * 1000; // Convertir en m√®tres
      const isInside = distance <= zone.radius;

      // V√©rifier si le membre √©tait d√©j√† dans la zone
      const wasInside = member.currentLocation ? 
        calculateDistance(member.currentLocation, zoneCenter) * 1000 <= zone.radius : false;

      // G√©n√©rer des √©v√©nements selon les transitions
      if (isInside && !wasInside && zone.notifications.onEnter) {
        const event: LocationEvent = {
          id: `event-${Date.now()}`,
          memberId,
          type: 'enter',
          zoneId: zone.id,
          location,
          timestamp: new Date(),
          message: `${member.name} est entr√© dans ${zone.name}`,
        };
        setLocationEvents(prev => [event, ...prev.slice(0, 99)]); // Garder les 100 derniers √©v√©nements
      } else if (!isInside && wasInside && zone.notifications.onExit) {
        const event: LocationEvent = {
          id: `event-${Date.now()}`,
          memberId,
          type: 'exit',
          zoneId: zone.id,
          location,
          timestamp: new Date(),
          message: `${member.name} a quitt√© ${zone.name}`,
        };
        setLocationEvents(prev => [event, ...prev.slice(0, 99)]);
      }

      // V√©rifier les restrictions d'acc√®s
      if (zone.allowedMembers && !zone.allowedMembers.includes(memberId) && isInside) {
        const event: LocationEvent = {
          id: `event-${Date.now()}`,
          memberId,
          type: 'alert',
          zoneId: zone.id,
          location,
          timestamp: new Date(),
          message: `‚ö†Ô∏è ${member.name} dans une zone restreinte: ${zone.name}`,
        };
        setLocationEvents(prev => [event, ...prev.slice(0, 99)]);
      }
    });
  }, [teamMembers, geofenceZones, calculateDistance]);

  const getAddressFromCoordinates = useCallback(async (lat: number, lng: number): Promise<string> => {
    try {
      // Utiliser une API de g√©ocodage inverse (exemple avec OpenStreetMap Nominatim)
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=18&addressdetails=1`
      );
      const data = await response.json();
      return data.display_name || `${lat.toFixed(6)}, ${lng.toFixed(6)}`;
    } catch (error) {
      console.error('Erreur g√©ocodage:', error);
      return `${lat.toFixed(6)}, ${lng.toFixed(6)}`;
    }
  }, []);

  const getNearbyMembers = useCallback((location: LocationData, radiusKm: number): TeamMember[] => {
    return teamMembers.filter(member => {
      if (!member.currentLocation || !member.isOnline) return false;
      const distance = calculateDistance(location, member.currentLocation);
      return distance <= radiusKm;
    });
  }, [teamMembers, calculateDistance]);

  const value: GeolocationContextType = {
    isLocationEnabled,
    currentLocation,
    locationError,
    isTracking,
    teamMembers,
    geofenceZones,
    locationEvents,
    enableLocation,
    disableLocation,
    startTracking,
    stopTracking,
    getCurrentPosition,
    updateMemberLocation,
    setMemberOnline,
    addGeofenceZone,
    updateGeofenceZone,
    removeGeofenceZone,
    checkGeofenceViolations,
    calculateDistance,
    getAddressFromCoordinates,
    getNearbyMembers,
  };

  return (
    <GeolocationContext.Provider value={value}>
      {children}
    </GeolocationContext.Provider>
  );
};

import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';
import { collection, addDoc, updateDoc, doc, query, where, getDocs, orderBy, Timestamp } from 'firebase/firestore';
import { db } from '../firebase';
import { PaymentRecord } from '../services/PaymentService';
import { useAuth } from './AuthContext';

interface PaymentContextType {
  payments: PaymentRecord[];
  isLoading: boolean;
  error: string | null;

  // Actions
  createPayment: (paymentData: Omit<PaymentRecord, 'id' | 'createdAt' | 'updatedAt'>) => Promise<string>;
  updatePaymentStatus: (paymentId: string, status: PaymentRecord['status'], flutterwaveRef?: string) => Promise<void>;
  getUserPayments: () => Promise<PaymentRecord[]>;
  getProjectPayments: (projectId: string) => Promise<PaymentRecord[]>;

  // Utilitaires
  calculateMonthlyRevenue: () => number;
  getPaymentStats: () => {
    total: number;
    successful: number;
    pending: number;
    failed: number;
    totalAmount: number;
  };
}

const PaymentContext = createContext<PaymentContextType | undefined>(undefined);

interface PaymentProviderProps {
  children: ReactNode;
}

export const PaymentProvider: React.FC<PaymentProviderProps> = ({ children }) => {
  const [payments, setPayments] = useState<PaymentRecord[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  /**
   * Cr√©e un nouveau paiement dans Firestore
   */
  const createPayment = useCallback(async (paymentData: Omit<PaymentRecord, 'id' | 'createdAt' | 'updatedAt'>): Promise<string> => {
    if (!user) {
      throw new Error('Utilisateur non connect√©');
    }

    setIsLoading(true);
    setError(null);

    try {
      const paymentRecord: Omit<PaymentRecord, 'id'> = {
        ...paymentData,
        userId: user.uid,
        createdAt: Timestamp.fromDate(new Date()),
        updatedAt: Timestamp.fromDate(new Date()),
      };

      const docRef = await addDoc(collection(db, 'payments'), paymentRecord);

      // Mettre √† jour l'√©tat local
      const newPayment: PaymentRecord = {
        ...paymentRecord,
        id: docRef.id,
      };

      setPayments(prev => [newPayment, ...prev]);

      console.log('‚úÖ Paiement cr√©√© avec succ√®s:', docRef.id);
      return docRef.id;
    } catch (err) {
      console.error('‚ùå Erreur lors de la cr√©ation du paiement:', err);
      setError('Erreur lors de la cr√©ation du paiement');
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [user]);

  /**
   * Met √† jour le statut d'un paiement
   */
  const updatePaymentStatus = useCallback(async (
    paymentId: string, 
    status: PaymentRecord['status'], 
    flutterwaveRef?: string
  ): Promise<void> => {
    setIsLoading(true);
    setError(null);

    try {
      const paymentRef = doc(db, 'payments', paymentId);
      const updateData: Partial<PaymentRecord> = {
        status,
        updatedAt: Timestamp.fromDate(new Date()),
      };

      if (flutterwaveRef) {
        updateData.flutterwaveRef = flutterwaveRef;
      }

      await updateDoc(paymentRef, updateData);

      // Mettre √† jour l'√©tat local
      setPayments(prev => prev.map(payment => 
        payment.id === paymentId 
          ? { ...payment, status, flutterwaveRef, updatedAt: Timestamp.fromDate(new Date()) }
          : payment
      ));

      console.log('‚úÖ Statut du paiement mis √† jour:', paymentId, status);
    } catch (err) {
      console.error('‚ùå Erreur lors de la mise √† jour du paiement:', err);
      setError('Erreur lors de la mise √† jour du paiement');
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * R√©cup√®re tous les paiements de l'utilisateur connect√©
   */
  const getUserPayments = useCallback(async (): Promise<PaymentRecord[]> => {
    if (!user) {
      throw new Error('Utilisateur non connect√©');
    }

    setIsLoading(true);
    setError(null);

    try {
      const q = query(
        collection(db, 'payments'),
        where('userId', '==', user.uid),
        orderBy('createdAt', 'desc')
      );

      const querySnapshot = await getDocs(q);
      const userPayments: PaymentRecord[] = [];

      querySnapshot.forEach((doc) => {
        userPayments.push({
          id: doc.id,
          ...doc.data()
        } as PaymentRecord);
      });

      setPayments(userPayments);
      console.log('‚úÖ Paiements utilisateur charg√©s:', userPayments.length);
      return userPayments;
    } catch (err) {
      console.error('‚ùå Erreur lors du chargement des paiements:', err);
      setError('Erreur lors du chargement des paiements');
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [user]);

  /**
   * R√©cup√®re tous les paiements d'un projet
   */
  const getProjectPayments = useCallback(async (projectId: string): Promise<PaymentRecord[]> => {
    setIsLoading(true);
    setError(null);

    try {
      const q = query(
        collection(db, 'payments'),
        where('projectId', '==', projectId),
        orderBy('createdAt', 'desc')
      );

      const querySnapshot = await getDocs(q);
      const projectPayments: PaymentRecord[] = [];

      querySnapshot.forEach((doc) => {
        projectPayments.push({
          id: doc.id,
          ...doc.data()
        } as PaymentRecord);
      });

      console.log('‚úÖ Paiements du projet charg√©s:', projectPayments.length);
      return projectPayments;
    } catch (err) {
      console.error('‚ùå Erreur lors du chargement des paiements du projet:', err);
      setError('Erreur lors du chargement des paiements du projet');
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * Calcule le chiffre d'affaires mensuel
   */
  const calculateMonthlyRevenue = useCallback((): number => {
    const now = new Date();
    const currentMonth = now.getMonth();
    const currentYear = now.getFullYear();

    return payments
      .filter(payment => {
        if (payment.status !== 'successful') return false;

        const paymentDate = payment.createdAt instanceof Date 
          ? payment.createdAt 
          : new Date(payment.createdAt.seconds * 1000);

        return paymentDate.getMonth() === currentMonth && 
               paymentDate.getFullYear() === currentYear;
      })
      .reduce((total, payment) => total + payment.amount, 0);
  }, [payments]);

  /**
   * Calcule les statistiques des paiements
   */
  const getPaymentStats = useCallback(() => {
    const stats = payments.reduce((acc, payment) => {
      acc.total++;
      acc[payment.status]++;

      if (payment.status === 'successful') {
        acc.totalAmount += payment.amount;
      }

      return acc;
    }, {
      total: 0,
      successful: 0,
      pending: 0,
      failed: 0,
      cancelled: 0,
      totalAmount: 0
    });

    return {
      total: stats.total,
      successful: stats.successful,
      pending: stats.pending,
      failed: stats.failed + stats.cancelled,
      totalAmount: stats.totalAmount
    };
  }, [payments]);

  const value: PaymentContextType = {
    payments,
    isLoading,
    error,
    createPayment,
    updatePaymentStatus,
    getUserPayments,
    getProjectPayments,
    calculateMonthlyRevenue,
    getPaymentStats,
  };

  return (
    <PaymentContext.Provider value={value}>
      {children}
    </PaymentContext.Provider>
  );
};

/**
 * Hook pour utiliser le contexte de paiement
 */
export const usePayment = (): PaymentContextType => {
  const context = useContext(PaymentContext);
  if (context === undefined) {
    throw new Error('usePayment must be used within a PaymentProvider');
  }
  return context;
};

export default PaymentContext;

