      // Supprimer les d√©penses associ√©es avant de supprimer le bon d'achat
      await BudgetIntegrationService.removePurchaseOrderExpenses(id);
      
      await PurchaseOrderService.deletePurchaseOrder(id);
      console.log('Contexte: Bon d\'achat supprim√© avec succ√®s:', id);
