const QUOTES_COLLECTION = 'quotes';

/**
 * Service Firebase pour la gestion des devis
 */
export class QuotesService {

    /**
     * Cr√©er un nouveau devis dans Firebase
     */
    static async createQuote(quote: Omit<Quote, 'id'>): Promise<string> {
        try {
            // Ne jamais stocker un champ "id" dans le document Firestore
            // m√™me si on nous passe un objet contenant potentiellement un id accidentellement.
            // On s'assure ici que le payload ne contient pas de cl√© id.
            // eslint-disable-next-line @typescript-eslint/no-unused-vars
            const { /* id: _ignoredId, */ ...safeQuote } = (quote as unknown) as Record<string, unknown>;

            const quoteData = {
                ...(safeQuote as Omit<Quote, 'id'>),
                createdAt: Timestamp.now(),
                updatedAt: Timestamp.now()
            };

            const docRef = await addDoc(collection(db, QUOTES_COLLECTION), quoteData);
            console.log('Devis cr√©√© avec ID:', docRef.id);
            return docRef.id;
        } catch (error) {
            console.error('Erreur lors de la cr√©ation du devis:', error);
            throw new Error('Impossible de cr√©er le devis');
        }

    }

    /**
     * R√©cup√©rer un devis par ID
     */
    static async getQuoteById(id: string): Promise<Quote | null> {
        try {
            const ref = doc(db, QUOTES_COLLECTION, id);
            const snap = await getDoc(ref);
            if (!snap.exists()) return null;
            const data = snap.data() as DocumentData;
            return {
                ...data,
                id: snap.id, // s'assurer que l'id du document Firestore pr√©vaut
                createdAt: data.createdAt?.toDate?.()?.toISOString() || data.createdAt,
                updatedAt: data.updatedAt?.toDate?.()?.toISOString() || data.updatedAt
            } as Quote;
        } catch (error) {
            console.error('Erreur lors de la r√©cup√©ration du devis par ID:', error);
            throw new Error('Impossible de r√©cup√©rer le devis');
        }
    }

    /**
     * R√©cup√©rer tous les devis
     */
    static async getAllQuotes(): Promise<Quote[]> {
        try {
            const q = query(
                collection(db, QUOTES_COLLECTION), 
                orderBy('createdAt', 'desc')
            );

            const querySnapshot = await getDocs(q);
            const quotes: Quote[] = [];

            querySnapshot.forEach((doc) => {
                const data = doc.data();
                quotes.push({
                    ...data,
                    id: doc.id, // id en dernier pour √©viter d'√™tre √©cras√© par data.id
                    createdAt: data.createdAt?.toDate?.()?.toISOString() || data.createdAt,
                    updatedAt: data.updatedAt?.toDate?.()?.toISOString() || data.updatedAt
                } as Quote);
            });

            return quotes;
        } catch (error) {
            console.error('Erreur lors de la r√©cup√©ration des devis:', error);
            throw new Error('Impossible de r√©cup√©rer les devis');
        }
    }

    /**
     * S'abonner en temps r√©el √† la liste des devis (tri√© par date de cr√©ation desc)
     */
    static subscribeToQuotes(callback: (quotes: Quote[]) => void): Unsubscribe {
        const q = query(
            collection(db, QUOTES_COLLECTION),
            orderBy('createdAt', 'desc')
        );
        return onSnapshot(q, (snapshot: QuerySnapshot) => {
            const quotes: Quote[] = snapshot.docs.map((d) => {
                const data = d.data() as DocumentData;
                return {
                    ...data,
                    id: d.id, // id en dernier
                    createdAt: data.createdAt?.toDate?.()?.toISOString() || data.createdAt,
                    updatedAt: data.updatedAt?.toDate?.()?.toISOString() || data.updatedAt
                } as Quote;
            });
            callback(quotes);
        });
    }

    /**
     * S'abonner en temps r√©el au compteur total des devis
     */
    static subscribeToQuoteCount(callback: (count: number) => void): Unsubscribe {
        const q = query(
            collection(db, QUOTES_COLLECTION)
        );
        return onSnapshot(q, (snapshot: QuerySnapshot) => {
            callback(snapshot.size);
        });
    }

    /**
     * R√©cup√©rer les devis par statut
     */
    static async getQuotesByStatus(status: string): Promise<Quote[]> {
        try {
            const q = query(
                collection(db, QUOTES_COLLECTION),
                where('status', '==', status),
                orderBy('createdAt', 'desc')
            );

            const querySnapshot = await getDocs(q);
            const quotes: Quote[] = [];

            querySnapshot.forEach((doc) => {
                const data = doc.data();
                quotes.push({
                    id: doc.id,
                    ...data,
                    createdAt: data.createdAt?.toDate?.()?.toISOString() || data.createdAt,
                    updatedAt: data.updatedAt?.toDate?.()?.toISOString() || data.updatedAt
                } as Quote);
            });

            return quotes;
        } catch (error) {
            console.error('Erreur lors de la r√©cup√©ration des devis par statut:', error);
            throw new Error('Impossible de r√©cup√©rer les devis');
        }
    }

    /**
     * Mettre √† jour un devis
     */
    static async updateQuote(quoteId: string, updates: Partial<Quote>): Promise<void> {
        try {
            const quoteRef = doc(db, QUOTES_COLLECTION, quoteId);
            // Ne jamais pousser 'id' (ou √©craser createdAt) dans Firestore
            // eslint-disable-next-line @typescript-eslint/no-unused-vars
            const { id: _ignoredId, createdAt: _ignoredCreatedAt, ...rest } = (updates as Partial<Quote>);
            const updateData = {
                ...rest,
                updatedAt: Timestamp.now()
            };

            await updateDoc(quoteRef, updateData);
            console.log('Devis mis √† jour:', quoteId);
        } catch (error) {
            console.error('Erreur lors de la mise √† jour du devis:', error);
            throw new Error('Impossible de mettre √† jour le devis');
        }
    }

    /**
     * Supprimer un devis
     */
    static async deleteQuote(quoteId: string): Promise<void> {
        try {
            const docRef = doc(db, QUOTES_COLLECTION, quoteId);
            await deleteDoc(docRef);
            console.log('Devis supprim√© avec succ√®s:', quoteId);
        } catch (error) {
            console.error('Erreur lors de la suppression du devis:', error);
            throw new Error('Impossible de supprimer le devis');
        }
    }

    /**
     * Rechercher des devis par nom de client
     */
    static async searchQuotesByClient(clientName: string): Promise<Quote[]> {
        try {
            // Note: Firestore ne supporte pas les recherches textuelles complexes
            // Cette m√©thode r√©cup√®re tous les devis et filtre c√¥t√© client
            const allQuotes = await this.getAllQuotes();

            return allQuotes.filter(quote => 
                quote.clientName.toLowerCase().includes(clientName.toLowerCase())
            );
        } catch (error) {
            console.error('Erreur lors de la recherche de devis:', error);
            throw new Error('Impossible de rechercher les devis');
        }
    }

    /**
     * Sauvegarder un devis (cr√©er ou mettre √† jour selon l'existence dans Firebase)
     */
    static async saveQuote(quote: Quote): Promise<string> {
        try {
            // Si le devis a un ID, v√©rifier s'il existe dans Firebase
            if (quote.id && quote.id.length > 0) {
                try {
                    const docRef = doc(db, QUOTES_COLLECTION, quote.id);
                    const docSnap = await getDoc(docRef);
                    if (docSnap.exists()) {
                        // Le document existe, on le met √† jour (exclure l'ID)
                        // eslint-disable-next-line @typescript-eslint/no-unused-vars
                        const { id: _id, ...updateData } = quote as Quote;
                        await this.updateQuote(quote.id, updateData as Partial<Omit<Quote, 'id'>>);
                        return quote.id;
                    } else {
                        // Le document n'existe pas, on le cr√©e avec l'ID sp√©cifi√©
                        // eslint-disable-next-line @typescript-eslint/no-unused-vars
                        const { id: _id, ...rest } = quote as Quote;
                        const quoteData: Omit<Quote, 'id' | 'createdAt' | 'updatedAt'> & { createdAt: Timestamp; updatedAt: Timestamp } = {
                            ...(rest as Omit<Quote, 'id' | 'createdAt' | 'updatedAt'>),
                            createdAt: Timestamp.now(),
                            updatedAt: Timestamp.now()
                        };
                        await setDoc(docRef, quoteData as unknown as DocumentData);
                        return quote.id;
                    }
                } catch (checkError) {
                    console.error('Erreur lors de la v√©rification du document:', checkError);
                    // En cas d'erreur de v√©rification, essayer de cr√©er un nouveau document
                    // eslint-disable-next-line @typescript-eslint/no-unused-vars
                    const { id: _id, ...quoteWithoutId } = quote as Quote;
                    const newId = await this.createQuote(quoteWithoutId as Omit<Quote, 'id'>);
                    return newId;
                }
            } else {
                // Pas d'ID, cr√©er un nouveau devis
                // eslint-disable-next-line @typescript-eslint/no-unused-vars
                const { id: _id, ...quoteWithoutId } = quote as Quote;
                const newId = await this.createQuote(quoteWithoutId as Omit<Quote, 'id'>);
                return newId;
            }
        } catch (error) {
            console.error('Erreur lors de la sauvegarde du devis:', error);
            throw new Error('Impossible de sauvegarder le devis');
        }
    }
}

